            $scope.processPricing = function (segmentIndex, legIndex) {

                // 0. get item in cart for the selected leg
                var items = [];
                _.forEach($scope.data.cart.items, function (item) {

                    if (item.SegmentIndex === segmentIndex && item.LegIndex === legIndex) {
                        item.CurrentSeatAmount = item.CurrentSeatCost;
                        item.IsChanged = (item.CurrentSeatNumber !== item.CSLSeatNumber) ? 1 : 0;
                        items.push(item);
                    }
                });

                var segmentSeatMap = $scope.data.selection.segmentSeatMaps[legIndex];
                // 1. Get is bundle
                var isBundle = $scope.getIsBundle(segmentSeatMap);//ADO233879 - Suyash -Start
                // 2. Get companion count
                var companionCount = $scope.getCompanionCount(segmentSeatMap);
                //Suyash - 73170
                var preferredZoneCompanionCount = $scope.getPreferredZoneCompanionCount(segmentSeatMap);

                // 3. Within a leg's item, calculated the chargeable amount of each item
                // 3.1 If it's bundle / aka absolute entitlement; all free (notes: it's either non or all travelers is bundle)
                if (isBundle) {
                    _.forEach(items, function (item) {
                        item.CurrentSeatAmount = 0;
                    });
                }
                else {
                    // 3.2 Based on privilege
                    // 3.2.1 IsEPlusSubscriber : free
                    // 3.2.2 IsOPT : no additional charge
                    _.forEach(items, function (item) {

                        var traveler = segmentSeatMap.Travelers[item.TravelerIndex];
                        if(traveler.hasOTPPurchasedOutsideJetstream){//Suyash - 282306
                            item.CurrentSeatAmount = 0;
                        }
                        if (traveler.IsEPlusSubscriber.hasEntitlements) {
                            item.CurrentSeatAmount = 0;
                        }
                        //ADO233879 - Suyash -Start
                        /*else if (traveler.hasPrivilege) {
                            item.CurrentSeatAmount = 0;
                        }*/
                        //ADO233879 - Suyash -Start 
                        else if (traveler.hasPrivilege.hasEntitlements && traveler.hasPrivilege.hasPrefProduct && item.JetstreamDisplaySeatType =='PREF') {
                            item.CurrentSeatAmount = 0;
                        }
                        else if (traveler.hasPrivilege.hasEntitlements && traveler.hasPrivilege.hasEconomyPlusProduct && item.JetstreamDisplaySeatType =='BLUE') {
                            item.CurrentSeatAmount = 0;
                        }//ADO233879 - Suyash - End
                        //Suyash - Cart differential calculation - Start
                        /*else if (traveler.IsOTP) {
                            item.CurrentSeatAmount = 0;
                        }*/
                        if(traveler.CSLSelectedSeat){//&& traveler.CSLSelectedSeat.SeatNumber Suyash - 282306
                            if(item.CurrentSeatAmount > traveler.CSLSelectedSeat.OTPSeatPrice){
                                item.CurrentSeatAmount = item.CurrentSeatAmount - traveler.CSLSelectedSeat.OTPSeatPrice;
                                item.isIssueAddCollect = true;
                                item.EDocTransactionIDForAddCollect = traveler.CSLSelectedSeat.EDocTransactionID;
                                //item.CurrentSeatCost = item.CurrentSeatAmount;
                                item.CurrentSeatRFISCCost = item.CurrentSeatAmount.toString();
                            }
                            else if(item.CurrentSeatAmount <= traveler.CSLSelectedSeat.OTPSeatPrice){
                            item.CurrentSeatAmount = 0;
                        }
                        }
                        //Suyash - Cart differential calculation - Start
                        // 2020-09-09. Story 472403. Suraphol.
                        //ADO233879 - Suyash -Start
                        /*else if (traveler.hasFareRelatedEntitlements) {
                            item.CurrentSeatAmount = 0;
                        }*/
                        if (traveler.hasFareRelatedEntitlements.hasEntitlements && traveler.hasFareRelatedEntitlements.hasPrefProduct && item.JetstreamDisplaySeatType =='PREF') {
                            item.CurrentSeatAmount = 0;
                        }
                        else if (traveler.hasFareRelatedEntitlements.hasEntitlements && traveler.hasFareRelatedEntitlements.hasEconomyPlusProduct && item.JetstreamDisplaySeatType =='BLUE') {
                            item.CurrentSeatAmount = 0;
                        }
                        else if (traveler.hasCorpPrefEntitlements.hasEntitlements && traveler.hasCorpPrefEntitlements.hasPrefProduct && item.JetstreamDisplaySeatType =='PREF') {
                            item.CurrentSeatAmount = 0;
                        }
                        else if (traveler.hasCorpPrefEntitlements.hasEntitlements && traveler.hasCorpPrefEntitlements.hasEconomyPlusProduct && item.JetstreamDisplaySeatType =='BLUE') {
                            item.CurrentSeatAmount = 0;
                        }//ADO233879 - Suyash - End
                    });

                    //Suyash - Start 73170
                    if (preferredZoneCompanionCount > 0) {
                        // 3.2.2.1 Sort order Amount
                        items = _.orderBy(items, ['CurrentSeatAmount', 'IsChanged'], ['desc', 'asc']);
                        _.forEach(items, function (item) {

                            if(item.JetstreamDisplaySeatType && item.JetstreamDisplaySeatType == 'PREF'){
                                if (item.CurrentSeatAmount === 0) return false;
                                item.CurrentSeatAmount = 0;
                                preferredZoneCompanionCount--;
                                if (preferredZoneCompanionCount <= 0) return false;
                            }
                        });
                    }
                    //Suyash - End 73170

                    // 3.2.3 If Companion count available; charge highest amount to 0
                    if (companionCount > 0) {
                        // 3.2.2.1 Sort order Amount
                        items = _.orderBy(items, ['CurrentSeatAmount', 'IsChanged'], ['desc', 'asc']);
                        _.forEach(items, function (item) {
                            if(item.JetstreamDisplaySeatType && item.JetstreamDisplaySeatType == 'BLUE'){
                            if (item.CurrentSeatAmount === 0) return false;
                            item.CurrentSeatAmount = 0;
                            companionCount--;
                            if (companionCount <= 0) return false;
                            }
                        });
                    }
                }

                $scope.data.cart.total = _.sumBy($scope.data.cart.items, 'CurrentSeatAmount');
            };
