            // 2021-11-23 Suyash End - Premier access - 7095
            $scope.reprocessPricing = function (segmentIndex, legIndex) {

                // 0.1 get item in succeed seat for the selected leg
                var items = [];
                    $scope.succeedSeatTotal = 0;
                _.forEach($scope.data.eplusResult.SucceedSeats, function (s) {

                    if (s.SegmentIndex === segmentIndex && s.LegIndex === legIndex) {

                        _.forEach($scope.data.cart.items, function (i) {

                            // 0.2 restore origin price; below will calculate again
                            if (i.SegmentIndex === s.SegmentIndex &&
                              i.LegIndex === s.LegIndex &&
                              i.TravelerIndex === s.TravelerIndex) {

                                s.Cost = i.CurrentSeatCost;
                                return false;
                            }
                        });

                        s.IsChanged = (s.CurrentSeat !== s.SelectedSeat) ? 1 : 0;
                        items.push(s);
                    }
                });

                var memory = null;
                _.forEach($scope.data.seatMapMemories, function (m) {

                    if (m.segmentIndex === segmentIndex) {
                        memory = m;
                        return false;
                    }
                });
                var isBundle = false;
                var companionCount = 0;
                var preferredZoneCompanionCount = 0;//ADO233879 - Suyash
                if (memory) {
                    var segmentSeatMap = memory.seatMaps[legIndex];
                    if (segmentSeatMap) {
                        // 1. Get is bundle
                        isBundle = $scope.getIsBundle(segmentSeatMap);
                        // 2. Get companion count
                        companionCount = $scope.getCompanionCount(segmentSeatMap);
                        preferredZoneCompanionCount = $scope.getPreferredZoneCompanionCount(segmentSeatMap);//ADO233879 - Suyash

                    }
                }

                // 3. Within a leg's item, calculated the chargeable amount of each item
                // 3.1 If it's bundle / aka absolute entitlement; all free (notes: it's either non or all travelers is bundle)
                if (isBundle) {
                    _.forEach(items, function (item) {
                        item.Cost = 0;
                    });
                }
                else {
                    // 3.2 Based on privilege
                    // 3.2.1 IsEPlusSubscriber : free
                    // 3.2.2 IsOPT : no additional charge
                    _.forEach(items, function (item) {

                        var traveler = segmentSeatMap.Travelers[item.TravelerIndex];
                        if (traveler.IsEPlusSubscriber.hasEntitlements) {
                            item.Cost = 0;
                        }
                        /*else if (traveler.hasPrivilege) {
                            item.Cost = 0;
                        }*/
                        //ADO233879 - Suyash -Start 
                        else if (traveler.hasPrivilege.hasEntitlements && traveler.hasPrivilege.hasPrefProduct && item.JetstreamDisplaySeatType =='PREF') {
                            item.Cost = 0;
                        }
                        else if (traveler.hasPrivilege.hasEntitlements && traveler.hasPrivilege.hasEconomyPlusProduct && item.JetstreamDisplaySeatType =='BLUE') {
                            item.Cost = 0;
                        }
                        /*else if (traveler.IsOTP) {
                            item.Cost = 0;
                        }*/
                        else if (traveler.hasFareRelatedEntitlements.hasEntitlements && traveler.hasFareRelatedEntitlements.hasPrefProduct && item.JetstreamDisplaySeatType =='PREF') {
                            item.Cost = 0;
                        }
                        else if (traveler.hasFareRelatedEntitlements.hasEntitlements && traveler.hasFareRelatedEntitlements.hasEconomyPlusProduct && item.JetstreamDisplaySeatType =='BLUE') {
                            item.Cost = 0;
                        }
                        else if (traveler.hasCorpPrefEntitlements.hasEntitlements && traveler.hasCorpPrefEntitlements.hasPrefProduct && item.JetstreamDisplaySeatType =='PREF') {
                            item.Cost = 0;
                        }
                        else if (traveler.hasCorpPrefEntitlements.hasEntitlements && traveler.hasCorpPrefEntitlements.hasEconomyPlusProduct && item.JetstreamDisplaySeatType =='BLUE') {
                            item.Cost = 0;
                        }//ADO233879 - Suyash - End
                    });
                    if (preferredZoneCompanionCount > 0) {
                        // 3.2.2.1 Sort order Amount
                        items = _.orderBy(items, ['Cost', 'IsChanged'], ['desc', 'asc']);
                        _.forEach(items, function (item) {

                            if(item.JetstreamDisplaySeatType && item.JetstreamDisplaySeatType == 'PREF'){
                                if (item.Cost === 0) return false;
                                item.Cost = 0;
                                preferredZoneCompanionCount--;
                                if (preferredZoneCompanionCount <= 0) return false;
                            }
                        });
                    }
                    // 3.2.3 If Companion count available; charge highest amount to 0
                    if (companionCount > 0) {

                        // 3.2.2.1 Sort order Amount
                        items = _.orderBy(items, ['Cost', 'IsChanged'], ['desc', 'asc']);
                        _.forEach(items, function (item) {
                            if(item.JetstreamDisplaySeatType && item.JetstreamDisplaySeatType == 'BLUE'){
                                if (item.Cost === 0) return false;
                                item.Cost = 0;
                                companionCount--;
                                if (companionCount <= 0) return false;
                            }
                        });
                    }
                }

                    // TFS - 473869 - 02/16/2021 - John Sanders
                    _.forEach($scope.data.eplusResult.SucceedSeats, function (succeedSeatAmount) {
                        //var tempAmount = parseInt(succeedSeatAmount.Cost);
                        var tempAmount = parseFloat(succeedSeatAmount.Cost);//Suyash - using parseFloat instead of ParseINT
                        $scope.succeedSeatTotal = $scope.succeedSeatTotal + tempAmount;
                    })
                    // TFS - 473869 - 02/16/2021 - John Sanders
                    // $scope.data.eplusResult.TotalCost = _.sumBy($scope.data.eplusResult.SucceedSeats, 'Cost');
                    $scope.data.eplusResult.TotalCost = $scope.succeedSeatTotal;
            };
