            function getNewStartDate(tempReq, monthsToDeduct, req){
                var endDate = new Date(tempReq.toDate);
                var startDate = new Date(req.fromDate);
                var toMonth = endDate.getMonth();
                var toYear = endDate.getFullYear();
                if(toMonth + 1 > monthsToDeduct){
                    endDate.setMonth(toMonth - monthsToDeduct);
                } else {
                    endDate.setMonth(toMonth - monthsToDeduct + 12);
                    endDate.setFullYear(toYear - 1);
                }
                var minDate = new Date(Math.max(endDate, startDate));
                var date;
                date = String(minDate.getFullYear());
                date = date.concat('-', String(minDate.getMonth() + 1).padStart(2,'0'));
                date = date.concat('-', String(minDate.getDate()).padStart(2,'0'));
                return date;
                //return startDate.getFullYear() + '-' + startDate.getMonth() > 9 ? startDate.getMonth() : '0' + startDate.getMonth() + '-' + startDate.getDate();
            }
            function deductDaysToDate(date, daysToDeduct){
                var deductedDate = new Date(date);
                deductedDate.setDate(deductedDate.getDate() - daysToDeduct);
                var date;
                date = String(deductedDate.getFullYear());
                date = date.concat('-', String(deductedDate.getMonth() + 1).padStart(2,'0'));
                date = date.concat('-', String(deductedDate.getDate()).padStart(2,'0'));
                return date;
            }
