    $scope.checkAndTrimInput = function(changedField) {
    // Get the current values of firstName and lastName
    var firstName = $scope.data.travelCertificateData.recipientInfo.firstName || '';
    var lastName = $scope.data.travelCertificateData.recipientInfo.lastName || '';
    
    // Calculate the combined length
    var combinedLength = firstName.length + lastName.length;

    if (combinedLength > 69) {
        // Trim the input to ensure the combined length doesn't exceed 69
        if (changedField === 'firstName') {
            var excessLength = combinedLength - 69;
            $scope.data.travelCertificateData.recipientInfo.firstName = firstName.slice(0, Math.max(0, firstName.length - excessLength));
        } else if (changedField === 'lastName') {
            var excessLength = combinedLength - 69;
            $scope.data.travelCertificateData.recipientInfo.lastName = lastName.slice(0, Math.max(0, lastName.length - excessLength));
        }
    }
};
