        (function () {
            'use strict';
            angular.module('portalModule')
                .controller('newsListController', controller);
            controller.$inject = ['$scope', '$state', 'CMSService', '$rootScope', 'util'];

            function controller($scope, $state, CMSService, $rootScope, util) {
                $scope.dataInfo = {
                    newList: [],
                    currentPage: 0,
                    isLastPage: false
                };
                $scope.loadMore = function () {
                    $scope.dataInfo.currentPage += 1;
                    CMSService.getViewAllNewsContent($scope.dataInfo.currentPage, $rootScope.currentLang).then(function (value) {
                        if (value && value[0]) {
                            $scope.dataInfo.currentPage = value[0].currentPage;
                            $scope.dataInfo.isLastPage = value[0].isLastPage;
                            for (var i = 0; i < value[0].newsList.length; i++) {
                                var sourceNews = value[0].newsList[i];
                                var newsWrapper = {};
                                newsWrapper.news = {};
                                newsWrapper.news.imgURL = sourceNews.news.ContentImageURL__c;
                                newsWrapper.news.SIP_Publish_Date__c = sourceNews.news.SIP_Publish_Date__c;
                                newsWrapper.news.Header__c = sourceNews.news.Homepage_Header__c;
                                newsWrapper.news.Title__c = sourceNews.news.Title__c;
                                newsWrapper.news.id = sourceNews.news.Id;
                                //abe add language 0824
                                newsWrapper.news.defaultLanguage = sourceNews.news.SIP_Default_Language__c;
                                var currentLang = !!$rootScope.currentUser ? $rootScope.currentUser.userLanguage : $rootScope.currentLang;
                                if (newsWrapper.news.defaultLanguage !== currentLang) {
                                    var notification = 'The {0} version of this content is being translated. The {1} version is shown below. Thank you for your patience.',
                                        currentDisplay = util.getLangDisplay(currentLang),
                                        defaultDisplay = util.getLangDisplay(newsWrapper.news.defaultLanguage);
                                    newsWrapper.news.notification = notification.replace('{0}', currentDisplay).replace('{1}', defaultDisplay);
                                    newsWrapper.news.showNotification = true;
                                } else {
                                    newsWrapper.news.notification = '';
                                    newsWrapper.news.showNotification = false;
                                }
                                $scope.dataInfo.newList.push(newsWrapper);
                            }
                        }
                    });
                };
                $scope.jumpToDetail = function (id) {
                    var id = { 'id': id };
                    $state.go('news-detail', id);
                };
                $scope.closeBanner = function (index) {
                    $scope.dataInfo.newList[index].news.showNotification = false;
                };
                $scope.loadMore();
            }
        })();
