                $scope.filterWithAppSelect = function (user) {
                    var flag = false
                    var appInfoList = [];
                    // get checked app into list
                    for (var key in $scope.manageApp) {
                        if ($scope.manageApp[key].value) {
                            appInfoList.push($scope.manageApp[key].name)
                        }
                    }

                    // super user have not umInfoList, but when all checkbox unchecked,filter will still show this user
                    if (!user.umInfoList) {
                        if (appInfoList.length === 0) {
                            flag = true
                        }
                    }

                    if (user.umInfoList) {
                        // only 'ACTIVE' umInfoApp involved in show and filter logic
                        var umInfoList = user.umInfoList.filter(function (item) {
                            return item.status === 'ACTIVE'
                        })

                        //eg: when A ,B checked,filter result must match both have A and B
                        //eg: when all checkbox unchecked , filter will show all user
                        var matchCount = 0
                        umInfoList.forEach(function (item) {
                            if (appInfoList.indexOf(item.appName) > -1) {
                                matchCount++
                            }
                        })

                        if (matchCount === appInfoList.length) {
                            flag = true
                        }

                    }

                    return flag
                };

