        (function () {
            'use strict';
            angular.module('portalModule')
                .controller('eCertificateRedemptionController', controller);
            controller.$inject = ['$scope', '$rootScope', 'eCertificateRedemptionService'];
            function controller($scope, $rootScope, eCertificateRedemptionService) {
                $scope.step = 1;
                $scope.offerCodePin = '';
                $scope.offerCode = '';
                $scope.pin = '';
                $scope.unitedConfirmationNumber = '';
                $scope.arc = '';
                $scope.errorNoOfferCode = false;
                $scope.errorStep2 = false;
                $scope.offer;
                $scope.resCodeError = false;
                $scope.result = {
                    success: '',
                    message: '',
                    xml: '',
                    resCode: '',
                    resSource: '',
                    resMessage: '',
                    passengers: []
                };
                $scope.submitStep1 = function () {
                    if ($scope.offerCodePin === '') {
                        $scope.errorNoOfferCode = true;
                    } else {
                        $rootScope.mainSpinner = true;
                        $scope.offerCode = $scope.offerCodePin.substring(0, 6).toUpperCase();
                        $scope.pin = $scope.offerCodePin.substring(6, $scope.offerCodePin.length).toUpperCase();
                        $scope.errorNoOfferCode = false;
                        eCertificateRedemptionService.getOffer($scope.offerCode, $rootScope.currentLang).then(function (value) {
                            if (value.length === 0) {
                                $rootScope.mainSpinner = false;
                                $rootScope.$broadcast('showMessage', {
                                    type: 'alert', text: 'No promo found'
                                });
                            } else {
                                $rootScope.mainSpinner = false;
                                $scope.step = 2;
                                $scope.offerCode = value[0].offerCode;
                                $scope.offer = {
                                    offerCode: value[0].offerCode,
                                    programName: value[0].programName,
                                    content: value[0].content,
                                    contentTranslation: value[0].contentTranslation,
                                    pdfTranslation: value[0].pdfTranslation,
                                    pdfLink: value[0].pdfLink
                                };
                            }
                        });
                    }
                };
                $scope.submitStep2 = function () {
                    $scope.result.passengers = [];
                    if ($scope.pin === '' || $scope.unitedConfirmationNumber === '' || $scope.arc === '') {
                        $scope.errorStep2 = true;
                    } else {
                        $rootScope.mainSpinner = true;
                        $scope.pin = $scope.pin.toUpperCase();
                        eCertificateRedemptionService.geteCertificateRedemptionNumber
                            ($scope.unitedConfirmationNumber, $scope.offerCode, $scope.pin, $scope.arc).then(function (value) {
                                $rootScope.mainSpinner = false;
                                $scope.result.success = value.success;
                                $scope.result.message = value.messages[0];
                                $scope.result.xml = value.xmlResponse;
                                if (value.success) {
                                    $scope.result.resCode = value.resCode;
                                    $scope.result.resSource = value.resSource;
                                    $scope.result.resMessage = value.resMessage;
                                    if (value.resCode === 'E0000') {
                                        $("html, body").animate({ scrollTop: 0 }, "slow");
                                        for (var i = 0; i < value.passengers.length; i++) {
                                            $scope.result.passengers.push({
                                                resultNumber: value.passengers[i].resultNumber,
                                                firstName: value.passengers[i].firstName,
                                                lastName: value.passengers[i].lastName,
                                                certNumber: value.passengers[i].certNumber
                                            });
                                        }
                                    } else {
                                        $rootScope.$broadcast('showMessage', {
                                            type: 'alert', text: 'Sorry, an error has occurred. Please contact United for further assistance.'
                                        });
                                    }
                                } else {
                                    $rootScope.$broadcast('showMessage', {
                                        type: 'alert', text: 'Sorry, an error has occurred. Please contact United for further assistance.'
                                    });
                                }
                            });
                    }
                };
                $scope.cancelBack = function () {
                    $scope.step = 1;
                    $scope.offerCodePin = '';
                    $scope.offerCode = '';
                    $scope.pin = '';
                    $scope.unitedConfirmationNumber = '';
                    $scope.arc = '';
                    $scope.errorNoOfferCode = false;
                    $scope.errorStep2 = false;
                    $scope.offer;
                    $scope.resCodeError = false;
                    $scope.result = {
                        success: '',
                        message: '',
                        xml: '',
                        resCode: '',
                        resSource: '',
                        resMessage: '',
                        passengers: []
                    };
                };
            }
        })();
