                    $scope.statementDateShow = $filter('date')($scope.curStatementDate.from, 'MM/dd/yyyy')+' - '+ $filter('date')($scope.curStatementDate.to, 'MM/dd/yyyy')
                    $scope.fetchTransTableData();
