                      //fix kevin 2020.12.17
                      svc.getUserList4Meeting = function (agencyId,keywords) {
                          var totalUserIdList = [];
                          var allUsers = [];
                          var deferred = $q.defer();
                          var hasAgencies,hasBranch,state
                          var errorMessages = ''

                          var load = function (totalUserIdList) {
                              $rootScope.mainSpinner = true;
                              svc.getMoreUserList4Meeting(agencyId, totalUserIdList,keywords).then(
                                function (val) {
                                    if(val){
                                        // console.log('getMoreUserList', val)
                                        allUsers = _.concat(allUsers, val.allUsers);
                                        hasAgencies = val.hasAgencies
                                        hasBranch = val.hasBranch
                                        state =val.state
                                        if(val.errorMessages){
                                            errorMessages = val.errorMessages[0] || ''
                                        }



                                        if (val.hasMore) {
                                            _(allUsers).forEach(function (item) {
                                                totalUserIdList.push(item.userId)
                                            });

                                            return load(totalUserIdList);
                                        } else {
                                            $rootScope.mainSpinner = false;
                                            deferred.resolve({
                                                allUsers: allUsers,
                                                state: state,
                                                hasBranch: hasBranch,
                                                hasAgencies:hasAgencies,
                                                errorMessages: errorMessages
                                            });
                                        }
                                    }else{
                                        deferred.reject({
                                            msg:'request failed'

                                        });
                                    }


                                }, function (err) {

                                    deferred.reject(err);
                                });
                          };
                          load(totalUserIdList, deferred);

                          return deferred.promise;
                      };
