      /* use case :
       *  <input type="text" class="form-control" ng-model="record.changeVal" only-digits-without-leading-zeros="true"/>
       *  restrict use to only input digit without dupilcate leading zeors
       * */
      return {
        restrict: 'A',
        require: '?ngModel',
        link: function (scope, element, attrs, ngModel) {

          ngModel.$parsers.push(function (inputValue) {
              inputValue += '';
              if (inputValue === undefined) {
                  return '';
              };

              var transformedInput = inputValue.replace(/(^0+(?!\.|$))|[^0-9]/g, '');
              var firstDot = inputValue.indexOf('.');

              if (firstDot !== -1) {
                  var arr = transformedInput.split('');
                  var round = arr.slice(0, firstDot);
                  var decimal = arr.slice(firstDot);

                  if (round.length > 14) {
                      round.splice(14);
                  };

                  decimal.splice(0, 0, '.');
                  if (decimal.length > 3) {
                      decimal.splice(3);
                  };

                  transformedInput = round.join('') + decimal.join('');
              } else {
                  var arr = transformedInput.split('');
                  if (arr.length > 14) {
                      arr.splice(14);
                  };
                  transformedInput = arr.join('');
              };

              if (transformedInput !== inputValue) {
                ngModel.$setViewValue(transformedInput);
                ngModel.$render();
              }
              return transformedInput;
          });
        },
      };
